def add_column(numb1, numb2):
    if numb1 > numb2:
        x = numb1
        y = numb2
    else:
        x = numb2
        y = numb1
    res = ''
    while x != 0:
        x1 = x % 10
        y1 = y % 10
        z = x1 + y1
        if z > 9:
            z %= 10
            res += str(z)
            x = int((x - x1) // 10 + 1)
            y = int((y - y1) // 10)
        else:
            res += str(z)
            x = int((x - x1) // 10)
            y = int((y - y1) // 10)
    return int(res[::-1])


