def add_column_all(numb1, numb2, n):
    if numb1 > numb2:
        x = int(numb1, n)
        y = int(numb2, n)
    else:
        x = int(numb2, n)
        y = int(numb1, n)
    num = ''
    while x != 0:
        x1 = x % 10
        y1 = y % 10
        z = x1 + y1
        if z > 9:
            z %= 10
            num += str(z)
            x = int((x - x1) // 10 + 1)
            y = int((y - y1) // 10)
        else:
            num += str(z)
            x = int((x - x1) // 10)
            y = int((y - y1) // 10)
    num = int(num[::-1])
    print(num)
    res = ''
    if (2 <= n <= 9):
        while num > 0:
            res = str(num % n) + res
            num //= n
    return res

